import styles from "./../assets/components/Service.module.css";
import ServidorIcon from "../assets/icons/jsxIcons/ServidorIcon";
import { Link } from "react-router-dom";

import endpoint from "../endpoint/UserStorage";

import { useState, useEffect, useReducer } from "react";
import Java from "../views/pages/Java";

function Service({
    nameService,
    logo,
    css,
    path,
    token,
}){
    const [state, dispatch] = useReducer(reducer, {image: "", content: "", graphic: null})

    function reducer(state, action){
        let memory = "";
        if(action.type === "mongodb" || action.type === "java"){
            if(typeof action.data[0].MemoryUse === "number"){
                memory = (action.data[0].MemoryUse / 1000).toFixed(2); 
            }
            return {
                ...state,
                image: <img src={logo} alt={logo} className={css}/>,
                content: (
                    <>
                        <h2>STATUS</h2>
                        {memory !== "" ? <p>Conectado</p> : <p>Desconectado</p>}
                        <h2>USO DA MEMÓRIA</h2>
                        {memory !== "" ? <p>{memory + "Gb"}</p> : <p>Erro</p>}
                    </>
                ),
                graphic: action.data,
            }
        } else {
            let flagMemory = false;
            let memoryPorcentage = "";
            if(typeof action.ArrayMemory[0].MemoryUse === "number" && typeof action.ArrayMemory[0].MemoryTotal === "number"){
                memory = (action.ArrayMemory[0].MemoryUse / 1000);
                memoryPorcentage = (memory / (action.ArrayMemory[0].MemoryTotal / 1000)) * 100;
                memory = parseFloat(memory).toFixed(2);
                memoryPorcentage = parseFloat(memoryPorcentage).toFixed(2);
                flagMemory = !flagMemory;
            }
            let flagDisk = false;
            let diskPorcentage = "";
            if(typeof action.ArrayCpuDisk[0].DiskUse === "number" && typeof action.ArrayCpuDisk[0].DiskTotal === "number"){
                diskPorcentage = ((action.ArrayCpuDisk[0].DiskUse / action.ArrayCpuDisk[0].DiskTotal) * 100).toFixed(2)
                flagDisk = !flagDisk;
            }
            console.log(action.ArrayMemory);
            console.log(action.ArrayCpuDisk);
            return {
                ...state,
                image: <div className={css}><ServidorIcon style={{size: 70}}/></div>,
                content: (
                    <>
                        <h2>USO DE DISCO</h2>
                        {flagDisk === true ? <p>{action.ArrayCpuDisk[0].DiskUse + "Gb (" + diskPorcentage + "%)"}</p> : <p>Erro</p>}
                        <h2>USO DA CPU/RAM</h2>
                        {(flagMemory === true)? <p>{action.ArrayCpuDisk[0].CpuUse + "% / " + memory + "Gb (" + memoryPorcentage + "%)"}</p> : <p>Erro</p>}
                    </>
                ),
                graphic: action.ArrayMemory,
            }
        }
    }

    useEffect(() => {
        const wait = async () => {
            let name = nameService.toLowerCase();
            if(name === "mongodb"){
                await mongodb(name);
            } else if(name === "java"){
                await java(name);
            } else if(name === "servidor"){
                await server(name);
            }
        }
        wait();
    }, []);

    const mongodb = async (name) => {
        return new Promise((resolve) => {
            endpoint.mongomemoryusegraph(token)
            .then(data => {
                if(data.length > 0){
                    dispatch({type: name, data: data});
                }
                resolve();
            })
            .catch(error => console.log(error));
        })
    }

    const java = async (name) => {
        return new Promise((resolve) => {
            endpoint.javamemoryusegraph(token)
            .then(data => {
                if(data.length > 0){
                    dispatch({type: name, data: data});
                }
                resolve();
            })
            .catch(error => console.log(error));
        })
    }

    const server = async (name) => {
        return new Promise((resolve) => {
            endpoint.servermemoryusegraph(token)
            .then(data1 => {
                endpoint.cpudiskusegraph(token)
                .then(data2 => {
                    if(data1.length > 0 && data2.length > 0){
                        dispatch({type: name, ArrayMemory: data1, ArrayCpuDisk: data2});
                    }
                    resolve();
                })
                .catch(error => console.log(error));
            })
            .catch(error => console.log(error));
        })
    }

    // useEffect(() => {
    //     if(nameService === "MongoDB" || nameService === "Java" || nameService === "Spring" || nameService === "Spark"){
    //         setImgService(<img src={logo} alt={logo} className={css}/>)
    //         if(nameService === "MongoDB"){
    //             memoryMongo();
    //         } else {
    //             memoryJava();
    //         }
    //     } else {
    //         setImgService(<div className={css}><ServidorIcon style={{size: 70}}/></div>);
    //         memoryServidor();
    //     }
    //     // eslint-disable-next-line
    // }, []);

    // useEffect(() => {
    //     if(nameService === "MongoDB" || nameService === "Java"){
    //         setInfoService(
    //             <>
    //                 <h2>STATUS</h2>
    //                 <p>Conectado/Desconectado</p>
    //                 <h2>USO DA MEMÓRIA</h2>
    //                 <p>{`${useMemory.Gb}Gb (${useMemory.porcentage}%)`}</p>
    //             </>
    //         )
    //     } else {
    //         endpoint.cpudiskusegraph(token)
    //         .then(data => {
    //             let useDisk = parseInt(data[0].DiskUse);
    //             let porcentage = ((useDisk/parseInt(data[0].DiskTotal))*100).toFixed(2);
    //             setInfoService(
    //                 <>
    //                     <h2>USO DE DISCO</h2>
    //                     <p>{`${useDisk}Gb (${porcentage}%)`}</p>
    //                     <h2>USO DA CPU/RAM</h2>
    //                     <p>{`${data[0].CpuUse}% / ${useMemory.Gb}Gb (${useMemory.porcentage}%)`}</p>
    //                 </>
    //             )
    //             if(graphic !== null){
    //                 let auxMemory = [];
    //                 let auxCpu = [];
    //                 let auxTime = [];
    //                 graphic.forEach(element => {
    //                     let aux = ((parseInt(element.MemoryUse)/parseInt(element.MemoryTotal))*100).toFixed(2);
    //                     auxMemory.push(aux);
    //                 })
    //                 data.forEach(element => {
    //                     auxCpu.push(element.CpuUse);
    //                     auxTime.push(element._id.date);
    //                 })
    //                 setGraphic({
    //                     cpu: auxCpu,
    //                     disk: data[0].DiskUse,
    //                     diskTotal: data[0].DiskTotal,
    //                     time: auxTime,
    //                     memory: auxMemory,
    //                 })
    //             }
    //         })
    //         .catch(error => console.log(error));
    //     }
    //     // eslint-disable-next-line
    // }, [useMemory]);

    return (state !== undefined) ? (
        <div className={styles.containerService}>
            {state.image}
            <div className={styles.title}>
              <p>{nameService}</p>  
            </div>
            <div className={styles.containerInfo}>
                {state.content}
            </div>
            <Link to={{pathname: path, state: {logo: logo, graphic: state.graphic, token: token}}}>
                <button>Ver mais</button>
            </Link>
        </div>
    ) : "";
}

export default Service;
