import Navigation from "./components/Navigation";
import endpoint from "./endpoint/SystemUsers";

import { useState, useEffect } from "react";

import './App.css';

function App() {
    const queryParameters = new URLSearchParams(window.location.search);
    const [isAdm, setIsAdm] = useState(false);
    const [finished, setFinished] = useState(false);
    const [token, setToken] = useState()

    useEffect(() => {
        let auxToken = queryParameters.get("token"); 
        if(auxToken){
            localStorage.setItem("token", auxToken);
            setToken(auxToken);    
        } else {
            setToken(localStorage.getItem("token"));
        }
        // eslint-disable-next-line
    }, [])

    const teste1 = async () => {
        return new Promise((resolve, reject) => {
                endpoint.infouser(token)
                .then(data => {
                    console.log("Entrou");
                    setTimeout(() => {
                        console.log("Pronto");
                        if(data.isAdmin){
                            setIsAdm(true);
                        } else {
                            alert("Apenas admin pode ter acesso a esta p치gina")
                        }
                        resolve();
                    }, 5000);
                })
                .catch((error) => {console.log(error); setFinished(true)});
        })
    }

    const ass = async () => {
        await teste1();
        console.log("Passou aqui");
    }

    useEffect(() => {
        endpoint.infouser(token)
        .then(data => {
            if(data.isAdmin){
                setIsAdm(true);
            } else {
                alert("Apenas admin pode ter acesso a esta p치gina")
            }})
        .catch((error) => {console.log(error); setFinished(true)});
    }, [token])

    return (isAdm && !finished) ? (
        <div className="App">
            <Navigation token={token}/>
        </div>
    ) : "";
}

export default App;
