import styles from "./../assets/components/Service.module.css";
import ServidorIcon from "../assets/icons/jsxIcons/ServidorIcon";
import { Link } from "react-router-dom";

import endpoint from "../endpoint/UsedMemory";

import { useState } from "react";
import { memo } from "react";

function Service({
    nameService,
    logo,
    css,
}){

    const [memory, setMemory] = useState(null);

    function imgService(){
        if(nameService === "MongoDB" || nameService === "Spring" || nameService === "Spark"){
            return <img src={logo} alt={logo} className={css}/>
        } else {
            return (
                <div className={css}>
                    <ServidorIcon style={{size: 70}}/>
                </div>
            )
        }
    }

    function useMemory(){
        if(nameService === "MongoDB"){
            endpoint.mongodiskuse()
            .then(data => {
                let aux = data/1000;
                setMemory(aux.toFixed(2));
            })
            .catch(error => console.log(error));
            return (
                <>
                    <h2>USO DA MEMÃ“RIA</h2>
                    <p>{memory + "GB"}</p>
                </>
            )
        }
        return;
    }

    return (
        <div className={styles.containerService}>
            {imgService()}
            <div className={styles.title}>
              <p>{nameService}</p>  
            </div>
            <div className={styles.containerInfo}>
                <h2>STATUS</h2>
                <p>Conectado/Desconectado</p>
                {useMemory()}
            </div>
            <Link to={{pathname: `/${nameService.toLowerCase()}`, state: {logo: logo}}}>
                <button>Ver mais</button>
            </Link>
        </div>
    )
}

export default Service;
