import Navigation from "./components/Navigation";
import endpoint from "./endpoint/SystemUsers";

import { useState, useEffect } from "react";

import './App.css';

function App() {
    const queryParameters = new URLSearchParams(window.location.search);
    const [isAdm, setIsAdm] = useState(false);
    const [finished, setFinished] = useState(false);
    const [token, setToken] = useState()

    // useEffect(() => {
    //     let auxToken = queryParameters.get("token"); 
    //     if(auxToken){
    //         localStorage.setItem("token", auxToken);
    //         setToken(auxToken);    
    //     } else {
    //         setToken(localStorage.getItem("token"));
    //     }
    //     // eslint-disable-next-line
    // }, [])

    // useEffect(() => {
    //     if(token){
    //         endpoint.infouser(token)
    //         .then(data => {
    //             if(data.isAdmin){
    //                 setIsAdm(true);
    //             } else {
    //                 alert("Apenas admin pode ter acesso a esta página")
    //             }
    //         })
    //         .catch((error) => {console.log(error); setFinished(true)});
    //     }
    // }, [token])

    var state = {
        name: null,
        lastName: null,
    };

    const teste1 = async () => {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                state = {
                    ...state,
                    name: "Giovane"
                }
                resolve();
            }, 2000);
        })
    }

    const teste2 = async () => {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                state = {
                    ...state,
                    lastName: "Limas Salvi"
                }
                resolve()
            }, 500);
        })
    }

    const funcTeste = async () => {
        await teste1()
        await teste2()
    }

    useEffect(() => {
        funcTeste();
    }, []);

    return (isAdm && !finished) ? (
        <div className="App">
            <Navigation token={token}/>
        </div>
    ) : "";
}

export default App;
