import styles from "./../assets/components/Service.module.css";
import ServidorIcon from "../assets/icons/jsxIcons/ServidorIcon";
import { Link } from "react-router-dom";

import endpoint from "../endpoint/UserStorage";
import DataParse from "../helpers/DataParse";

import { useState, useEffect, useReducer } from "react";
function Service({
    nameService,
    logo,
    css,
    path,
    token,
}){
    const [imgService, setImgService] = useState("");
    const [infoService, setInfoService] = useState("");
    const [useMemory, setUseMemory] = useState("");
    const [graphic, setGraphic] = useState(null);

    const [state, dispatch] = useReducer(reducer, {image: "", memory: "", graphic: null, info: "", porcentage: ""})

    async function mongodb(state){
        await endpoint.mongomemoryusegraph(token)
        .then(data => {
            if(data && typeof data === "object"){
                let element = data[0];
                if(element.MemoryTotal){
                    state = {
                        ...state,
                        porcentage: (DataParse.parseMemory(element.MemoryUse) / DataParse.parseMemory(element.MemoryTotal))*100 + "%"
                    }
                }
                return {
                    image: (<img src={logo} alt={logo} className={css}/>),
                    graphic: data,
                    memory: DataParse.parseMemory(element.MemoryUse) + "Gb", 
                }
            }
        }).catch(error => {console.log(error); return null});
    }

    function reducer(state, action){
        if(action.type === "java"){
            return {
                image: (<img src={logo} alt={logo} className={css}/>)
            }
        } else if(action.type === "mongodb"){
            let object = mongodb();
            if(object){
                return {
                    image: object.image,
                    graphic: object.graphic,
                    memory: object.memory,
                    info: (
                        <>
                            <h2>STATUS</h2>
                            <p>Conectado</p>
                            <h2>USO DA MEMÓRIA</h2>
                            {state.porcentage !== "" ? (<p>{`${object.memory} (${object.porcentage})`}</p>) : (<p>{object.memory}</p>)}
                        </>
                    ) 
                }   
            }
        } else if(action.type === "servidor"){
            return {
                image: (<div className={css}><ServidorIcon style={{size: 70}}/></div>)
            }
        } else {
            return ""
        }
    }

    useEffect(() => {
        if(typeof nameService === "string"){
            let object = null;

            dispatch({type: nameService.toLowerCase(), object: object})
        }
    }, []);


    function memoryMongo(){
        endpoint.mongomemoryusegraph(token)
        .then(data => {
            setGraphic(data);
            let total = parseInt(data[0].MemoryTotal);
            let memoryUse = parseInt(data[0].MemoryUse);
            let porcentage = ((memoryUse/total)*100).toFixed(2);
            setUseMemory({
                Gb: (memoryUse/1000).toFixed(2),
                porcentage: porcentage,
            });
        })
        .catch(error => console.log(error));
    }

    function memoryJava(){
        endpoint.javamemoryusegraph(token)
            .then(data => {
                setGraphic(data)
                let total = parseInt(data[0].MemoryTotal);
                let memoryUse = parseInt(data[0].MemoryUse);
                let porcentage = ((memoryUse/total)*100).toFixed(2);
                setUseMemory({
                    Gb: (memoryUse/1000).toFixed(2),
                    porcentage: porcentage,
                });
        })
        .catch(error => console.log(error));
    }

    function memoryServidor(){
        endpoint.servermemoryusegraph(token)
        .then(data => {
            setGraphic(data);
            let total = parseInt(data[0].MemoryTotal);
            let memoryUse = parseInt(data[0].MemoryUse);
            let porcentage = ((memoryUse/total)*100).toFixed(2);
            setUseMemory({
                Gb: (memoryUse/1000).toFixed(2),
                porcentage: porcentage,
            });
        })
        .catch(error => console.log(error));
    }  

    useEffect(() => {
        // if(nameService === "MongoDB" || nameService === "Java" || nameService === "Spring" || nameService === "Spark"){
        //     setImgService(<img src={logo} alt={logo} className={css}/>)
        //     if(nameService === "MongoDB"){
        //         memoryMongo();
        //     } else {
        //         memoryJava();
        //     }
        // } else {
        //     setImgService(<div className={css}><ServidorIcon style={{size: 70}}/></div>);
        //     memoryServidor();
        // }
        // eslint-disable-next-line
    }, []);

    useEffect(() => {
        if(nameService === "MongoDB" || nameService === "Java"){
            // setInfoService(
            //     <>
            //         <h2>STATUS</h2>
            //         <p>Conectado/Desconectado</p>
            //         <h2>USO DA MEMÓRIA</h2>
            //         <p>{`${useMemory.Gb}Gb (${useMemory.porcentage}%)`}</p>
            //     </>
            // )
        } else {
            // endpoint.cpudiskusegraph(token)
            // .then(data => {
            //     let useDisk = parseInt(data[0].DiskUse);
            //     let porcentage = ((useDisk/parseInt(data[0].DiskTotal))*100).toFixed(2);
            //     setInfoService(
            //         <>
            //             <h2>USO DE DISCO</h2>
            //             <p>{`${useDisk}Gb (${porcentage}%)`}</p>
            //             <h2>USO DA CPU/RAM</h2>
            //             <p>{`${data[0].CpuUse}% / ${useMemory.Gb}Gb (${useMemory.porcentage}%)`}</p>
            //         </>
            //     )
            //     if(graphic !== null){
            //         let auxMemory = [];
            //         let auxCpu = [];
            //         let auxTime = [];
            //         graphic.forEach(element => {
            //             let aux = ((parseInt(element.MemoryUse)/parseInt(element.MemoryTotal))*100).toFixed(2);
            //             auxMemory.push(aux);
            //         })
            //         data.forEach(element => {
            //             auxCpu.push(element.CpuUse);
            //             auxTime.push(element._id.date);
            //         })
            //         setGraphic({
            //             cpu: auxCpu,
            //             disk: data[0].DiskUse,
            //             diskTotal: data[0].DiskTotal,
            //             time: auxTime,
            //             memory: auxMemory,
            //         })
            //     }
            // })
            // .catch(error => console.log(error));
        }
        // eslint-disable-next-line
    }, [useMemory]);

    return (state !== undefined) ? (
            <div className={styles.containerService}>
                {/* {imgService} */}
                {state.image}
                <div className={styles.title}>
                <p>{nameService}</p>  
                </div>
                <div className={styles.containerInfo}>
                    {/* {infoService} */}
                    {state.info}
                </div>
                <Link to={{pathname: path, state: {logo: logo, graphic: state.graphic, token: token}}}>
                    <button>Ver mais</button>
                </Link>
            </div>
        ) : "";
}

export default Service;
