import Navigation from "./components/Navigation";
import endpoint from "./endpoint/SystemUsers";

import { useState, useEffect } from "react";

import './App.css';

function App() {
    const queryParameters = new URLSearchParams(window.location.search);
    const [isAdm, setIsAdm] = useState(false);
    const [finished, setFinished] = useState(false);
    const [token, setToken] = useState()

    useEffect(() => {
        let auxToken = queryParameters.get("token"); 
        if(auxToken){
            localStorage.setItem("token", auxToken);
            setToken(auxToken);    
        } else {
            setToken(localStorage.getItem("token"));
        }
        // eslint-disable-next-line
    }, [])

    useEffect(() => {
        if(token){
            teste1();
        }
    }, [token])

    const teste1 = async () => {
        return new Promise((resolve, reject) => {
            setTimeout(() => {
                endpoint.infouser(token)
                .then(data => {
                    if(data.isAdmin){
                        setIsAdm(true);
                    } else {
                        alert("Apenas admin pode ter acesso a esta página")
                    }
                })
                .catch((error) => {console.log(error); setFinished(true)});
                resolve();
            }, 5000);
        })
    }

    return (isAdm && !finished) ? (
        <div className="App">
            <Navigation token={token}/>
        </div>
    ) : "";
}

export default App;
