import styles from "./../../assets/views/pages/UsersSystem.module.css";
import { FiSearch } from "react-icons/fi";
import { useState, useEffect } from "react";
import { PopUp } from "../../components/PopUp";
import InfoUser from "../popups/InfoUser";
import endpoint from "./../../endpoint/SystemUsers";
import DataParse from "../../helpers/DataParse";

function UsersSystem({token}){

    const [trigger, setTrigger] = useState();
    const [systemUsers, setSystemUsers] = useState([])
    const [user, setUser] = useState(null)
    //const [lastAccess, setLastAccess] = useState([]);

    useEffect(() => {
        setTrigger(false)
            const wait = async () => {
                return new Promise((resolve) => {
                    endpoint.systemusers(token)
                    .then(data => {setSystemUsers(data); resolve()})
                    .catch(error => console.log(error));
                })
            }
            wait();
        // eslint-disable-next-line
    }, []);

    useEffect(() => {
        let length = 0;
            let auxUsers = [];
            if(systemUsers.length > 0){
                systemUsers.forEach(user => {
                    endpoint.useraccess(token, user.userId)
                    .then(data => {
                        console.log(data);
                        if(data.length > 0){
                            let time = DataParse.parseDate(data[0].date);
                            if(time){
                                auxUsers.push({
                                    ...user,
                                    lastTimeOnline: time.day + " Ã s " + time.hour,
                                    lastAction: data[0].jobType,
                                })
                            } else {
                                auxUsers.push(user);
                            }
                        } else {
                            auxUsers.push(user);
                        }
                        length++;
                        if(length === systemUsers.length){
                            setSystemUsers(auxUsers);
                        }
                    })
                    .catch(error => console.log(error))
                })

                //setSystemUsers(auxUsers);
            }
    }, [systemUsers]);

    console.log(systemUsers);

    return (systemUsers !== undefined) ?
        (
            <>
            <div className={styles.containerUsersSystem}>
                <div className={styles.title}>
                    <p>UsuÃ¡rios do Sistema</p>
                    <p>Qntd. de UsuÃ¡rios: {systemUsers !== [] ? systemUsers.length : 0}</p>
                </div>
                <div className={styles.containerSearch}>
                    <div className={styles.input}>
                        <input type="text" placeholder="Pesquisar pelo nome do usuÃ¡rio" onChange={(e) => console.log(e.target.value)}></input>
                        <FiSearch />
                    </div>
                </div>
                <div className={styles.containerContent}>
                    <header className={styles.header}>
                        <p>usuÃ¡rio</p>
                        <span></span>
                        <p>armazenamento utilizada</p>
                        <span></span>
                        <p>tipo de aÃ§Ã£o</p>
                        <span></span>
                        <p>data</p>
                    </header>

                    <div className={styles.logs}>
                        {systemUsers.map((systemUser, index) => {
                            return (
                                <div className={styles.log} onClick={() => {setTrigger(true); setUser(systemUser);}} key={index}>
                                    <p>{systemUser.firstName + " " + systemUser.lastName}</p>
                                    <p>{systemUser.mongoDiskUsageMB !== 0 ? systemUser.mongoDiskUsageMB + "MB" : ""}</p>
                                    <p></p>
                                    <p></p>
                                </div>
                            )
                        })}
                    </div>
                </div>
            </div>
            {trigger && (
                <PopUp trigger={trigger} setTrigger={setTrigger} title="InformaÃ§Ãµes do usuÃ¡rio e histÃ³rico de acesso">
                    <InfoUser user={user} token={token}/>
                </PopUp>
            )}
        </>
    ) : "";
}

export default UsersSystem;
