import DataParse from "../helpers/DataParse";
import http from "./http-common";

//função que retorna as informações do usuário pelo token
function infouser(token){
    return http
        .post("/getuser/", {token})
        .then(response => {return response.data})
        .catch(error => console.log(error));
}

const newUser = (token, user) => {
    let ready = false;
    useraccess(token, user.userId)
    .then(userData => {
        console.log(userData);
        ready =  !ready;
    })
    .catch(error => console.log(error));
    let id = setInterval(frame, 100);
    function frame(){
        if(ready){
            clearInterval(id);
            console.log("passou")
        }
    }
}

//função que retorna todos os usuários do sistema
function systemusers(token){
    return http
        .post("/getsystemusers/", {
            token,
        })
        .then(response => {
            let length = 0;
            const wait = async (data) => {
                let auxData = [];
                data.forEach(user => {
                    useraccess(token, user.userId)
                    .then(userData => {
                        if(userData.length > 0){
                            auxData.push({
                                ...user,
                                lastTimeOnline: userData[0].date,
                                lastAction: userData[0].jobType,
                            })
                        } else {
                            auxData.push(user);
                        }
                        length++;
                    })
                    .catch(error => console.log(error));
                    if(length === data.lenght){
                        console.log("asdjfoasfjiasejf")
                        return auxData;
                    }
                })
            }

            if(response.data.length > 0){
                let data = response.data;
                const teste = async () => {
                    await wait(data);
                }
                teste();
            }
            return [];
        })
        .catch(error => console.log(error));
}

//função que retorna todos os usuários online
function systemlogs(token){
    // eslint-disable-next-line 
    return http.
        post("/getloggedusers/", {
            token,
        })
        .then(response => {
            return response.data;
        })
        .catch(error => console.log(error));
}

//função que retorna os registros do sistema de um usuário em específico pelo seu ID
function useraccess(token, id){
    return http.post("/getsystemlogsbyuserid/", {
        token,
        id,
    })
    .then(response => {return response.data})
    .catch(error => console.log(error))
}

const endpoint = {
    systemusers,
    systemlogs,
    useraccess,
    infouser,
};

export default endpoint;
