import http from "./http-common";
import DataParse from "../helpers/DataParse";

//função que retorna as informações do usuário pelo token
function infouser(token){
    return http
        .post("/getuser/", {token})
        .then(response => {return response.data})
        .catch(error => console.log(error));
}

//função que retorna todos os usuários do sistema
function systemusers(token){
    return http
        .post("/getsystemusers/", {
            token,
        })
        .then(response => {
            if(response.data){
                var newAux = null;
                let auxData = [];
                const teste = async (userId) => {
                    return new Promise((resolve) => {
                        useraccess(token, userId)
                        .then(data => {
                            if(data !== undefined){
                                console.log(data);
                                let time = null//DataParse.parseDate(data[0].date);
                                if(time){
                                    newAux = {
                                        time: time.day + " às " + time.hour,
                                        action: data.jobType,
                                    }
                                }
                            } 
                            resolve();
                        })
                        .catch(error => console.log(error));
                    })
                }

                const wait = async (user) => {
                    await teste(user.userId);
                    return newAux;
                }
                response.data.forEach(user => {
                    let aux = wait(user);
                    auxData.push({
                        ...user,
                        lastTimeOnline: aux.time,
                        lastAction: aux.action,
                    })
                })
                return auxData;
            }
        })
        .catch(error => console.log(error));
}

//função que retorna todos os usuários online
function systemlogs(token){
    // eslint-disable-next-line 
    return http.
        post("/getloggedusers/", {
            token,
        })
        .then(response => {
            return response.data;
        })
        .catch(error => console.log(error));
}

//função que retorna os registros do sistema de um usuário em específico pelo seu ID
function useraccess(token, id){
    return http.post("/getsystemlogsbyuserid/", {
        token,
        id,
    })
    .then(response => {return response.data})
    .catch(error => console.log(error))
}

const endpoint = {
    systemusers,
    systemlogs,
    useraccess,
    infouser,
};

export default endpoint;
