import styles from "./../assets/pages/Servidor.module.css";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, Title, CategoryScale, LinearScale, PointElement, LineElement, Filler } from "chart.js"
import { Doughnut, Line } from "react-chartjs-2";
import { useState, useEffect } from "react";

function Servidor(){
    const [graphicLeftData, setGraphicLeftData] = useState(null)
    const [graphicRightData, setGraphicRightData] = useState(null)
    const [graphicCenterData, setGraphicCenterData] = useState(null)

    useEffect(() => {
        ChartJS.register(CategoryScale, LinearScale, PointElement, ArcElement, LineElement, Tooltip, Legend, Title, Filler);
        //Iniaizaliza os dados e as opções de cada gráfico
        let auxLeft = {data: {
            labels: ["Disponível(Gb)", "Utilizado(Gb)"],
            datasets: [{
                label: "Disponível(Gb)",
                data: ["2000", "3000"],
                backgroundColor: ['rgba(220, 220, 220)', 'rgba(0, 92, 200)']
            }]
        }, options: {
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
                title: {
                    text: "Uso de disco: partição/app", display: true
                },
            },
        }, plugins: {
            id: "textCenter",
            beforeDatasetsDraw(chart, args, pluginOptions){
                const {ctx, data} = chart;
                ctx.save();
                ctx.font = "200 15px sans-serif";
                ctx.fillStyle = "rgba(0, 92, 200)";
                ctx.textAlign = "center";
                ctx.textBaseline = "middle";
                ctx.fillText("Utilizado", chart.getDatasetMeta(0).data[0].x,  chart.getDatasetMeta(0).data[0].y);
                //ctx.fillText("30%", chart.getDatasetMeta(0).data[0].x,  chart.getDatasetMeta(0).data[0].y);
            }
        }}
        setGraphicLeftData(auxLeft);
        let auxRight = {data: {
            labels: ["Disponível(Gb)", "Utilizado(Gb)"],
            datasets: [{
                label: "Disponível(Gb)",
                data: ["5000", "3000"],
                backgroundColor: ['rgba(220, 220, 220)', 'rgba(0, 92, 200)']
            }]
        }, options: {
            plugins: {
                title: {
                    text: "Uso de disco: partição/dados", display: true
                }
            }
        }}
        setGraphicRightData(auxRight);
        let auxCenter = {data: null, options: null}
        auxCenter.data = {
            labels: ["2016", "2017", "2018", "2019", "2020"],
            datasets: [{
                label: "CPU %",
                data: ["2000", "5000", "1500", "3200", "1200"],
                backgroundColor: 'rgba(255, 0, 0, 0.2)',
                borderColor: 'rgba(255, 0, 0, 0.6)',
                fill: true,
                pointRadius: 0,
                lineTension: 0.2
            }, {
                label: 'RAM %',
                data: ["1000", "2000", "1500", "2000", "3500"],
                backgroundColor: 'rgba(0, 0, 255, 0.2)',
                borderColor: 'rgba(0, 0, 255, 0.6)',
                fill: true,
                pointRadius: 0,
                lineTension: 0.2
            }]
        }
        auxCenter.options = {
            maintainAspectRatio: false,
            responsive: true,
            plugins: {
                title: {
                    display: true,
                    text: "Uso CPU e RAM"
                },
                legend: {
                    position: "top"
                },
            },
        }
        setGraphicCenterData(auxCenter)
    }, []); 

    return (    
        <div style={{background: "#eee"}}>
            <div className={styles.containerServidor}>
                <p>Informações do Sercidor</p>
                <div className={styles.contentServidor}>
                    <div className={styles.useDisk}>
                        <div>
                            {graphicLeftData && <Doughnut data={graphicLeftData.data} options={graphicLeftData.options} plugins={[graphicLeftData.plugins]}/>}
                        </div>
                        <div>
                            {graphicRightData && <Doughnut data={graphicRightData.data} options={graphicRightData.options}/>}
                        </div>
                    </div>
                    <div className={styles.useGraphic}>
                        {graphicCenterData && <Line data={graphicCenterData.data} options={graphicCenterData.options}/>}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Servidor;
